<?phpnamespace RushPHP\dispatcher;class ShellDispatcher extends DispatcherBase{	/**	 * shell.php --domain=aaa.ddd.ddd --act=Index.setTime --get="uid=123456&unanme=koocyton"	 */	public function __construct()	{        if (empty($_REQUEST['act']) || !preg_match('/^([a-z_]+)\.([a-z_]+)$/i', $_REQUEST['act'], $matchs))		{			$this->class_name  = "Index";			$this->method_name = "main";		}		else        {        	$this->class_name  = $matchs[1];        	$this->method_name = $matchs[2];        }		$_REQUEST['act'] = $this->class_name . '.' . $this->method_name;	}}